import React from 'react'
import styled from 'styled-components';

const Wrapper = styled.div`
  position: fixed;
  width: 100%;
  height: 100%;
  overflow-y: hidden;
`;

const Overlay = styled.div`
  position: fixed;
  width: 100%;
  z-index: 999;
  align-items: center;
  justify-content: center;
  overflow-y: hidden;
`;

const NeedWarrantyForm = styled.div`
  position: relative;
  display: flex;
  font-weight: 700;
  font-size: 16px;
  line-height: 20px;
  margin: 0 auto;
  width: 70%;
  overflow-y: scroll;
`;

const FormInner = styled.div`
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  position: fixed;
  background-color: #FFF;
  width: 70%;
  padding: 14.56px 21.84px 5.46px;
  gap: 22px;
  border-radius: 15px;
  border: 2px solid black;
`;

const Title = styled.div`
  text-align: center;
  flex: none;
  align-self: stretch;
  
`;

const CloseButton = styled.button`
  display: flex;
  flex-direction: row;
  justify-content: center;
  align-items: right;
  margin-left: auto;
  background: #307FE2;
  border: none;
  border-radius: 4px;
  color: rgba(255, 255, 255, 1);
`;

const InputForm = styled.div`
  width: 100%;
  display: flex;
  flex-direction: column;
  border-bottom: 2px solid rgba(0, 0, 0, 0.38);
  input{
    box-sizing: border-box;
    align-items: flex-start;
    width: 100%;
    height: 50px;
    background: #FFFFFF;
    padding-left: 10px;
    border: 1px solid rgba(36, 89, 173, 1);
    border-radius: 10px;
    &:focus {
      outline: 2px solid rgba(36, 89, 173, 1);
    }
  }
`;

const Label = styled.label`
  align-items: center;
  padding: 0px;
  gap: 4.85px;
  width: 402.32px;
  height: 21px;
`;

const Span = styled.span`
  color: red;
`;

const Button = styled.button`
  display: flex;
  flex-direction: row;
  justify-content: center;
  align-items: center;
  padding: 10px 20px;
  gap: 10px;
  width: auto;
  height: 40px;
  background: #307FE2;
  border-radius: 8px;
  border: none;
  font-family: 'Roboto';
  font-style: normal;
  font-weight: 400;
  font-size: 16px;
  line-height: 19px;
  color: rgba(255, 255, 255, 1);
  margin: 0 auto;
`;

const Block = styled.div`
  display: block;
  padding: 20px 0px 0px 0px;
`;

const Left = styled.div`
  display: inline-block;
  width: 50%;
  text-align: left;
  label {
    display: flex;
    text-align: start;
  }
  input{
    box-sizing: border-box;
    align-items: flex-start;
    width: 100%;
    height: 50px;
    background: #FFFFFF;
    padding-left: 10px;
    border: 1px solid rgba(36, 89, 173, 1);
    border-radius: 10px;
    &:focus {
      outline: 2px solid rgba(36, 89, 173, 1);
    }
  }
`;
const Right = styled.div`
  display: inline-block;
  width: 50%;
  text-align: right;
  label {
    display: flex;
    text-align: start;
    padding-left: 5%;
  }
  input{
    box-sizing: border-box;
    align-items: flex-start;
    width: 95%;
    height: 50px;
    background: #FFFFFF;
    padding-left: 10px;
    border: 1px solid rgba(36, 89, 173, 1);
    border-radius: 10px;
    &:focus {
      outline: 2px solid rgba(36, 89, 173, 1);
    }
  }
`;
function NeedWarranty({open, onClose}) {
  
  return (
    <Wrapper>
      <Overlay shouldCloseOnOverlayClick={false}>
      <NeedWarrantyForm >
        <FormInner>
          <CloseButton onClick={onClose}>X</CloseButton>
          <Title>Yêu cầu bảo hành, sửa chữa</Title>
          <InputForm>
            <Label>Họ và Tên <Span>*</Span></Label>
            <input id="fullname" name="fullname" placeholder="Nguyễn Văn A"/>
            <Block>
              <Left>
                <Label>Email<Span>*</Span></Label>
                <input type="email" name="email" placeholder="abc@hcmut.edu.vn" />
              </Left>
              <Right>
                <label>Số điện thoại</label>
                <input type="text" id="phone" name="phone" placeholder="0948566534"/>
              </Right>
            </Block>
            <Label>Địa chỉ<Span> *</Span></Label>
            <input type="text" id="adr" name="address" placeholder="Đại học Bách Khoa"/>
          </InputForm>

          <InputForm>
            <Label>Số Serial sản phẩm<Span> *</Span></Label>
            <input type="text" id="sr" name="serial" placeholder="Đại học Bách Khoa" />
            <Label>Tên sản phẩm</Label>
            <input type="text" id="name-product" name="product" placeholder="Đại học Bách Khoa" />
            <Label>Thương hiệu</Label>
            <input type="text" id="trademark" name="trademark" placeholder="Đại học Bách Khoa" />
            <Label>Mô tả vấn đề của sản phẩm</Label>
            <input type="text" id="description" name="description" placeholder="Đại học Bách Khoa" />
          </InputForm>
            
        <Button>Gửi yêu cầu</Button>
        </FormInner>
        
      </NeedWarrantyForm>
    </Overlay>
    </Wrapper>
    
  )
}

export default NeedWarranty
