import React, {useState} from 'react'
import '../css/detail.css';
import styled from 'styled-components';
import {Link} from 'react-router-dom';
import closeSearch from '../icon-font/close-icon.svg';
import product from './SearchData';
import ProductCompare from './ProductCompare';
import Iframe from 'react-iframe';

const CompareForm = styled.div`
    background-color: #fff;
    box-shadow: 1px 1px 1px 1px #ccc;
    border-radius: 20px;
    padding-bottom: 10px;
    height: 100%;
    width: 50%;   
    padding: 16px 16px 16px 16px;
    gap: 16px;
`;

const InputForm = styled.div`
    padding: 0px;
    gap: 9.7px;
    max-width: 100%;
    display: inline;
    flex-direction: column;
    align-items: flex-end;
    input {
        box-sizing: border-box;
        align-items: flex-start;
        width: 100%;
        height: 50px;
        border: 1px solid rgba(36, 89, 173, 1);
        border-radius: 10px;
        &:focus {
            outline: 2px solid rgba(36, 89, 173, 1);
        }
    }
`
const LabelLine = styled.div`
    display: flex;
    flex-direction: row;
    justify-content: space-between;
    align-items: center;
    padding: 0px;
    gap: 4.85px;
    height: 24px;
    margin-bottom: 10px;
`;

const LabelSearch = styled.label`
    
    font-style: normal;
    font-weight: 700;
    font-size: 16px;
    line-height: 20px;
    margin-bottom: 0px;
`;

const SearchResults = styled.div`
    width: 100%;
    height: 200px;
    background-color: white;
    box-shadow: rgba(0, 0, 0, 0.35) 0px 5px 15px;
    overflow: hidden;
    overflow-y: auto;
    &::-webkit-scrollbar {
        display: none;
      }
`;

const DataItem = styled(Link)`
    width: 100%;
    height: 70px;
    display: flex;
    align-items: center;
    color: black;
    text-decoration: none;
    cursor: pointer;
    &:hover {
        background-color: lightgrey;
      }
    img {
        max-width:100%;
        max-height:100%;
    }
`;

const LeftIframe = styled(Iframe)`
    border: none;
    &::-webkit-scrollbar {
        display: none;
      }
`;

/*const SearchButton = styled.button`
    display: flex;
    flex-direction: row;
    justify-content: center;
    align-items: center;
    padding: 10px 10px;
    gap: 10px;
    width: auto;
    height: 40px;
    background: #307FE2;
    border-radius: 8px;
    border: none;
    font-family: 'Roboto';
    font-style: normal;
    font-weight: 400;
    font-size: 16px;
    line-height: 19px;
    color: rgba(255, 255, 255, 1);
    margin: 0 auto;
    cursor: pointer;
`; */



function Compare({open, onClose}) {

    const [filteredData, setFilteredData] = useState([]);
    const [wordEntered, setWordEntered] = useState(""); 

    const handleFilter = (event) => {
        const searchWord = event.target.value;
        setWordEntered(searchWord);
        const newFilter = product.filter((value) => {
          return value.name.toLowerCase().includes(searchWord.toLowerCase());
        });
    
        if (searchWord === "") {
          setFilteredData([]);
        } else {
          setFilteredData(newFilter);
        }
      };
    
    const [show, setShow] = useState(true)

    const handledClick = () => {
        onClose()
        if(!open) {
            setShow(current => false)
        }
        setShow(!show)
    }
    if(!open) return null
    return (
        <>
            {show && <CompareForm>
                <InputForm shouldCloseOnOverlayClick={false}>
                    <LabelLine>
                        <LabelSearch>Nhập tên sản phẩm</LabelSearch>
                        <img onClick={handledClick} src={closeSearch} className="close-outline-btn" alt=""/>
                    </LabelLine>
                        <input 
                            type="text" 
                            value={wordEntered}
                            placeholder="Robot..."
                            onChange={handleFilter}
                        />
                        {filteredData.length !== 0 && (
                            <SearchResults>
                                {filteredData.slice(0, 15).map((value, key) => {
                                    const handleClick = () => {
                                        setShow(false)
                                    }                              
                                    return (
                                        <DataItem to="" onClick={handleClick} target="_blank">
                                            <img src={value.image} alt="" />
                                            <p>{value.name}</p>
                                        </DataItem>
                                    )
                                })}
                            </SearchResults>
    
                            )}
                </InputForm>               
            </CompareForm> 
            }
            
            
        </>
    )
}


export default Compare
