import React from 'react'
import styled from 'styled-components';


const Service = styled.div`
`;

const Container = styled.div`
    display: flex;
    justify-content: space-evenly;
`;

const ServiceItem = styled.div`
    border: #000 solid 2px;
    border-radius: 10px;
    box-shadow: 1px 1px 1px 1px rgba(174, 171, 171, 0.5);
    width: 20%;
    text-align: center;
`;

const TiImage = styled.div`
    border-radius: 10px;
    width: 100%;
    height: 70%;
`;

const TiName = styled.div`
    padding: 20px;
    font-weight: 700;
    font-size:20px;
`;


function ServiceWr() {
  return (
    <div className="service">
        <div className="chapter">
            <div className="chapter-line"/>
            <div className="chapter-name">Dịch Vụ Bảo Hành</div>
        </div>

        <div className="service-container">
            <div className="service-item">
                <img src={require("../img/service/search.png")} alt="" className="Ti-image" />
                <div className="Ti-name">Tra cứu bảo hành</div>
            </div>
            <div className="service-item">
                <img src={require("../img/service/reason03.png")} alt="" className="Ti-image" />
                <div className="Ti-name">Yêu cầu bảo hành, sửa chửa</div>
            </div>
            <div className="service-item">
                <img src={require("../img/service/zalo.png")} alt="" className="Ti-image" />
                <div className="Ti-name">Hướng dẫn</div>
            </div>
        </div>
    </div>
  )
}

export default Service
