import React from 'react'
import '../css/detail.css';
import styled from 'styled-components';
import close from '../icon-font/close-icon.svg';

const CompareForm = styled.div`
    background-color: #fff;
    box-shadow: 1px 1px 1px 1px #ccc;
    border-radius: 20px;
    padding-bottom: 10px;
    padding-left: 40px;
    height: 100%;
    max-width: 50%;   
    display: inline-block;
    flex-direction: column;
    align-items: flex-end;
    padding: 16px;
    gap: 16px;
`;

const InputForm = styled.div`
    padding: 0px;
    gap: 9.7px;
    max-width: 100%;
    
    input {
        box-sizing: border-box;
        align-items: flex-start;
        width: 100%;
        height: 50px;
        border: 1px solid rgba(36, 89, 173, 1);
        border-radius: 10px;
        &:focus {
            outline: 2px solid rgba(36, 89, 173, 1);
        }
    }
`
const LabelLine = styled.div`
    display: inline-flex;
    flex-direction: row;
    justify-content: space-between;
    align-items: center;
    padding: 0px;
    gap: 4.85px;
    height: 24px;
    label {
        font-family: 'Mulish';
        font-style: normal;
        font-weight: 700;
        font-size: 16px;
        line-height: 20px;
    }
`;

const Label = styled.label`
    
`;

function Compare() {
  return (
    <CompareForm>
        <InputForm>
            <LabelLine>
                <label>Nhập tên sản phẩm</label>
                <img src={close} className="close-outline-btn" alt=""/>
            </LabelLine>
            <input type="text" id="name-product" name="name-product" placeholder="ro bốt hút bụi"/>
        </InputForm>
    </CompareForm>
    
  )
}

export default Compare
