import React, {useState} from 'react'
import '../css/detail.css';
import styled from 'styled-components';
import closeSearch from '../icon-font/close-icon.svg';
import product from './SearchData';

const Right = styled.div`
    display: inline-flex;
    
`;

const CompareForm = styled.div`
    background-color: #fff;
    box-shadow: 1px 1px 1px 1px #ccc;
    border-radius: 20px;
    padding-bottom: 10px;
    height: 100%;
    width: 50%;   
    padding: 16px 16px 16px 16px;
    gap: 16px;
`;

const InputForm = styled.div`
    padding: 0px;
    gap: 9.7px;
    max-width: 100%;
    display: inline;
    flex-direction: column;
    align-items: flex-end;
    input {
        box-sizing: border-box;
        align-items: flex-start;
        width: 100%;
        height: 50px;
        border: 1px solid rgba(36, 89, 173, 1);
        border-radius: 10px;
        &:focus {
            outline: 2px solid rgba(36, 89, 173, 1);
        }
    }
`
const LabelLine = styled.div`
    display: flex;
    flex-direction: row;
    justify-content: space-between;
    align-items: center;
    padding: 0px;
    gap: 4.85px;
    height: 24px;
    margin-bottom: 10px;
`;

const LabelSearch = styled.label`
    
    font-style: normal;
    font-weight: 700;
    font-size: 16px;
    line-height: 20px;
    margin-bottom: 0px;
`;

const SearchButton = styled.button`
    display: flex;
    flex-direction: row;
    justify-content: center;
    align-items: center;
    padding: 10px 10px;
    gap: 10px;
    width: auto;
    height: 40px;
    background: #307FE2;
    border-radius: 8px;
    border: none;
    font-family: 'Roboto';
    font-style: normal;
    font-weight: 400;
    font-size: 16px;
    line-height: 19px;
    color: rgba(255, 255, 255, 1);
    margin: 0 auto;
    cursor: pointer;
`;

const SearchResults = styled.div`
    margin-top: 5px;
    width: 300px;
    height: 200px;
    background-color: white;
    box-shadow: rgba(0, 0, 0, 0.35) 0px 5px 15px;
    overflow: hidden;
    overflow-y: auto;
`;

const DataItem = styled.a`
    width: 100%;
    height: 50px;
    display: flex;
    align-items: center;
    color: black;
`;

function Compare({open, onClose}) {

    const [filteredData, setFilteredData] = useState([]);
    const [wordEntered, setWordEntered] = useState("");

    if(!open) return null
    return (
        <Right>
            <CompareForm>
                <InputForm shouldCloseOnOverlayClick={false}>
                    <LabelLine>
                        <LabelSearch>Nhập tên sản phẩm</LabelSearch>
                        <img onClick={onClose} src={closeSearch} className="close-outline-btn" alt=""/>
                    </LabelLine>
                    <input type="text" id="product-name" name="product-name" placeholder="robot hút bụi" />
                </InputForm>
                <SearchButton>
                    Tìm kiếm
                </SearchButton>
                
                
            </CompareForm>
            <SearchResults>
                    {product.map((value, key) => {
                        return (
                            <DataItem href="" target="blank">
                                <p>{value.name}</p>
                            </DataItem>
                        )
                    })}
            </SearchResults>
        </Right>
    )
}

export default Compare
