import React from 'react'
import styled from 'styled-components';


const Container = styled.div`
    display: flex;
    justify-content: space-evenly;
`;

const ServiceItem = styled.div`
    border: #000 solid 2px;
    border-radius: 10px;
    box-shadow: 1px 1px 1px 1px rgba(174, 171, 171, 0.5);
    width: 25%;
    text-align: center;
`;

const TiImage = styled.img`
    border-radius: 10px;
    width: 100%;
    height: 70%;
`;

const TiName = styled.div`
    padding: 20px;
    font-weight: 700;
    font-size:20px;
`;


function Service() {
  return (
    <div className="service">
        <div className="chapter">
            <div className="chapter-line"/>
            <div className="chapter-name">Dịch Vụ Bảo Hành</div>
        </div>

        <Container>
            <ServiceItem>
                <TiImage src={require("../img/service/search.png")} ></TiImage>
                <TiName>Tra cứu bảo hành</TiName>
            </ServiceItem>
            <ServiceItem>
                <TiImage src={require("../img/service/reason03.png")} ></TiImage>
                <TiName>Yêu cầu bảo hành, sửa chửa</TiName>
            </ServiceItem>
            <ServiceItem>
                <TiImage src={require("../img/service/zalo.png")} ></TiImage>
                <TiName>Hướng dẫn</TiName>
            </ServiceItem>
        </Container>
    </div>
  )
}

export default Service
