import React, {useState} from 'react';
import styled from 'styled-components';
import SearchWarranty from './SearchWarranty';


const Main = styled.div`
    padding-bottom: 200px;
`;

const Chapter = styled.div`
    position: relative;
    display: flex;
    justify-content: center;
    margin: 100px 0;
`;

const ChapterLine = styled.div`
    position: absolute;
    border: #b4b4b4 solid 1px;
    width: 100%;
    top: 20px;
`;

const ChapterName = styled.div`
    position: absolute;
    z-index: 1;
    height: 30px;
    background-color: #fff;
    border: #b4b4b4 solid 1px;
    text-align: center;
    font-size: 22px;
    font-weight: 900;
    padding: 5px;
`;
const Container = styled.div`
    display: flex;
    justify-content: space-evenly;
`;

const ServiceItem = styled.div`
    border: #000 solid 2px;
    border-radius: 10px;
    box-shadow: 1px 1px 1px 1px rgba(174, 171, 171, 0.5);
    width: 23%;
    text-align: center;
`;

const TiImage = styled.img`
    border-radius: 10px;
    max-width: 100%;
    height: auto;
    padding-top: 40px;
`;

const TiName = styled.div`
    padding: 20px;
    font-weight: 700;
    font-size:20px;
`;

function Service() {
    const [buttonPopup, setButtonPopup] = useState(false);
  return (
    <Main>
        <Chapter>
            <ChapterLine />
            <ChapterName>Dịch vụ bảo hành</ChapterName>
        </Chapter>

        <Container>
            <ServiceItem onClick={() => setButtonPopup(true)}>
                <TiImage src={require("../img/service/search.png")}></TiImage>
                <TiName>Tra cứu bảo hành</TiName>
            </ServiceItem>

            

            <ServiceItem>
                <TiImage src={require("../img/service/reason03.png")}></TiImage>
                <TiName>Yêu cầu bảo hành, sữa chữa</TiName>
            </ServiceItem>

            <ServiceItem>
                <TiImage src={require("../img/service/zalo.png")}></TiImage>
                <TiName>Hướng dẫn</TiName>
            </ServiceItem>
        </Container>

        <SearchWarranty trigger={buttonPopup} setTrigger={setButtonPopup}>
                <h1>Form</h1>
                <p>asdddddddddddddddddddasdas</p>
        </SearchWarranty>
    </Main>
    
  )
}

export default Service
