import React from 'react'
import styled from 'styled-components';

const Content = styled.div`
  position: fixed;
  width: 100%;
  display: flex;
  z-index: 2;
  background-color: blue;
`;

const NeedWarrantyForm = styled.div`
    position: relative;
    font-family: 'Mulish';
    font-style: normal;
    font-weight: 700;
    font-size: 16px;
    line-height: 20px;
    width: 70%;
    margin:0 auto;
    `;

const FormInner = styled.div`
    display: flex;
    flex-direction: column;
    align-items: flex-start;
    position: fixed;
    padding:  21.84px 5.46px;
    gap: 22px;
    width: 70%;
    background-color: #FFFFFF;
    border-radius: 15px;
    border: 2px solid black;
`;

const Title = styled.div`
  text-align: center;
  flex: none;
  align-self: stretch;
`;

const CloseButton = styled.button`
  display: flex;
  flex-direction: row;
  justify-content: center;
  align-items: right;
  margin-left: auto;
  background: #307FE2;
  border: none;
  border-radius: 4px;
  color: rgba(255, 255, 255, 1);
`;

function NeedWarranty({open, onClose}) {
    if(!open) return null
    return (
        <Content>
            <NeedWarrantyForm>
                <FormInner>
                    <CloseButton>X</CloseButton>
                    <Title>Yêu cầu bảo hành, sửa chữa</Title>
                </FormInner>
            </NeedWarrantyForm>
        </Content>
    )
}

export default NeedWarranty
