import React from 'react'
import styled from 'styled-components';

const Content = styled.div`
  position: fixed;
  width: 100%;
  display: flex;
  z-index: 2;
  background-color: blue;
`;

const NeedWarrantyForm = styled.div`
    position: relative;
    font-style: normal;
    font-weight: 700;
    font-size: 16px;
    line-height: 20px;
    width: 70%;
    margin:0 auto;
    `;

const FormInner = styled.div`
    display: flex;
    flex-direction: column;
    align-items: flex-start;
    position: fixed;
    padding: 14.56px 21.84px 5.46px;
    gap: 22px;
    width: 70%;
    background-color: #FFFFFF;
    border-radius: 15px;
    border: 2px solid black;
`;

const Title = styled.div`
  text-align: center;
  flex: none;
  align-self: stretch;
`;

const CloseButton = styled.button`
  display: flex;
  flex-direction: row;
  justify-content: center;
  align-items: right;
  margin-left: auto;
  background: #307FE2;
  border: none;
  border-radius: 4px;
  color: rgba(255, 255, 255, 1);
`;

const InputForm = styled.div`
  display: flex;
  flex-direction: column;
  border-bottom: #ccc solid 1px;
  input {
    box-sizing: border-box;
    align-items: flex-start;
    width: 100%;
    height: 50px;
    background: #FFFFFF;
    padding-left: 10px;
    border: 2px solid rgba(36, 89, 173, 1);
    border-radius: 10px;
    &:focus {
      outline: none;
    }
  }
`;

const Label = styled.label`
  align-items: center;
  padding: 0px;
  gap: 4.85px;
  width: 402.32px;
  height: 21px;
`;

const Span = styled.span`
  color: red;
`;

const Button = styled.button`
  display: flex;
  flex-direction: row;
  justify-content: center;
  align-items: center;
  padding: 10px 10px;
  gap: 10px;
  width: 100px;
  height: 40px;
  background: #307FE2;
  border-radius: 8px;
  border: none;
  font-family: 'Roboto';
  font-style: normal;
  font-weight: 400;
  font-size: 16px;
  line-height: 19px;
  color: rgba(255, 255, 255, 1);
  margin: 0 auto;
`;

function NeedWarranty({open, onClose}) {
    if(!open) return null
    return (
        <Content>
            <NeedWarrantyForm>
                <FormInner>
                    <CloseButton>X</CloseButton>
                    <Title>Yêu cầu bảo hành, sửa chữa</Title>
                    <InputForm>
                        <Label>Họ và tên <Span> *</Span></Label>
                        <input type="text" id="fullname" name="fullname" placeholder="Nguyễn Văn A"/>
                    </InputForm>
                </FormInner>
            </NeedWarrantyForm>
        </Content>
    )
}

export default NeedWarranty
