import React from 'react'
import styled from 'styled-components';
import Header from './Header';
const Form = styled.div`
  width: 100%;
  margin: 0;
  background-color: blue;
`;

const FormInner = styled.div`
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  position: fixed;
  padding: 14.56px 21.84px 5.46px;
  gap: 21.84px;
  width: 446px;
  height: 219.21px;
  box-shadow: 0px 2.73px 7.28px rgba(132, 132, 134, 0.24);
  border-radius: 15px;
  border: 2px solid black;
`;

const Title = styled.div`
  text-align: center;
  flex: none;
  order: 0;
  align-self: stretch;
  flex-grow: 0;
`;

const InputForm = styled.div`
  display: flex;
  flex-direction: column;
  border-bottom: #ccc solid 1px;
  input {
    box-sizing: border-box;
    align-items: flex-start;
    width: 400px;
    height: 50px;
    background: #FFFFFF;
    padding-left: 10px;
    border: 2px solid rgba(36, 89, 173, 1);
    border-radius: 10px;
    &:focus {
      outline: none;
    }
  }
`;

const Label = styled.label`
  align-items: center;
  padding: 0px;
  gap: 4.85px;
  width: 402.32px;
  height: 21px;
`;

/*const PhoneInput = styled.input`
  box-sizing: border-box;
  align-items: flex-start;
  padding: 0px;
  width: 400px;
  height: 50px;
  background: #FFFFFF;
  border: 1.21286px solid rgba(36, 89, 173, 1);
  border-radius: 25%;
`;*/

const Span = styled.span`
  color: red;
`;

const Button = styled.button`
  display: flex;
  flex-direction: row;
  justify-content: center;
  align-items: center;
  padding: 10px 10px;
  gap: 10px;
  width: 100px;
  height: 40px;
  background: #307FE2;
  border-radius: 8px;
  border: none;
  font-family: 'Roboto';
  font-style: normal;
  font-weight: 400;
  font-size: 16px;
  line-height: 19px;
  color: rgba(255, 255, 255, 1);
  margin: 0 auto;
`;


const SearchWarranty = ({open, onClose}) => {
  
  return (
    <>
    
    <Header />
    <Form>
        <FormInner>
          <Title>Tra cứu thông tin bảo hành</Title>
          <InputForm>
            <Label>Nhập số điện thoại/ số Serial sản phẩm<Span> *</Span></Label>
            <input type="text" name="phone" placeholder="0948566534" />
          </InputForm>         
          <Button>Tra cứu</Button>
        </FormInner>
    </Form>
    </>
  );
}

export default SearchWarranty
