import React from 'react'
import styled from 'styled-components';

const Form = styled.div`
  font-family: 'Mulish';
  font-style: normal;
  font-weight: 700;
  font-size: 16px;
  line-height: 20px;
`;

const FormInner = styled.div`
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  padding: 14.56px 21.84px 5.46px;
  gap: 21.84px;

  position: relative;
  width: 446px;
  height: 219.21px;
  background: #FFFFFF;
  box-shadow: 0px 2.73px 7.28px rgba(132, 132, 134, 0.24);
  border-radius: 15px;
`;

const Title = styled.div`
  text-align: center;
  flex: none;
  order: 0;
  align-self: stretch;
  flex-grow: 0;
`;

const Label = styled.label`
  display: flex;
  flex-direction: row;
  align-items: center;
  padding: 0px;
  gap: 4.85px;
  width: 402.32px;
  height: 21px;
`;

const PhoneInput = styled.input`
  box-sizing: border-box;
  display: flex;
  flex-direction: row;
  align-items: flex-start;
  padding: 0px;
  width: 400px;
  height: 50px;
  background: #FFFFFF;
  border: 1.21286px solid rgba(36, 89, 173, 1);
  border-radius: 25%;
`;

const Span = styled.span`
  color: red;
`;

const Button = styled.button`
  display: flex;
  flex-direction: row;
  justify-content: center;
  align-items: center;
  padding: 10px 10px;
  gap: 10px;
  width: 94px;
  height: 39px;
  background: #307FE2;
  border-radius: 8px;
  border: none;
font-family: 'Roboto';
font-style: normal;
font-weight: 400;
font-size: 16px;
line-height: 19px;
color: rgba(255, 255, 255, 1);

`;


function SearchWarranty(props) {
  return (
    <Form>
        <FormInner>
          <Title>Tra cứu thông tin bảo hành</Title>
          <Label>Nhập số điện thoại/ số Serial sản phẩm<Span> *</Span></Label>
          <PhoneInput type="text" name="phone" placeholder="0948566534" />      
          <Button>Tra cứu</Button>
        </FormInner>
    </Form>
  );
}

export default SearchWarranty
