import React from 'react'
import '../css/detail.css';
import styled from 'styled-components';

const CompareForm = styled.div`
    background-color: #fff;
    box-shadow: 1px 1px 1px 1px #ccc;
    border-radius: 20px;
    padding-bottom: 10px;
    padding-left: 40px;
    height: 100%;
    max-width: 50%;   
    display: inline-block;
    flex-direction: column;
    align-items: flex-end;
    padding: 16px;
    gap: 16px;
`;

const InputForm = styled.div`
    position: absolute;
    padding: 0px;
    gap: 9.7px;
    width: 30%;
`
const LabelLine = styled.div`
    display: flex;
    flex-direction: row;
    justify-content: space-between;
    align-items: center;
    padding: 0px;
    gap: 4.85px;
    height: 24px;
`;

const Label = styled.label`
    font-family: 'Mulish';
    font-style: normal;
    font-weight: 700;
    font-size: 16px;
    line-height: 20px;
`;

function Compare() {
  return (
    <CompareForm>
        <InputForm>
            <LabelLine>
                <Label>Nhập tên sản phẩm</Label>
            </LabelLine>
        </InputForm>
    </CompareForm>
    
  )
}

export default Compare
