import React, {useState} from 'react'
import '../css/detail.css';
import styled from 'styled-components';
import {Link, useParams} from 'react-router-dom';
import closeSearch from '../icon-font/close-icon.svg';
import data from './SearchData';
import ProductCompare from './ProductCompare';
import Iframe from 'react-iframe';

const CompareForm = styled.div`
    background-color: #fff;
    box-shadow: 1px 1px 1px 1px #ccc;
    border-radius: 20px;
    padding-bottom: 10px;
    height: 100%;
    width: 50%;   
    padding: 16px 16px 16px 16px;
    gap: 16px;
`;

const InputForm = styled.div`
    padding: 0px;
    gap: 9.7px;
    max-width: 100%;
    display: inline;
    flex-direction: column;
    align-items: flex-end;
    input {
        box-sizing: border-box;
        align-items: flex-start;
        width: 100%;
        height: 50px;
        border: 1px solid rgba(36, 89, 173, 1);
        border-radius: 10px;
        &:focus {
            outline: 2px solid rgba(36, 89, 173, 1);
        }
    }
`
const LabelLine = styled.div`
    display: flex;
    flex-direction: row;
    justify-content: space-between;
    align-items: center;
    padding: 0px;
    gap: 4.85px;
    height: 24px;
    margin-bottom: 10px;
`;

const LabelSearch = styled.label`
    
    font-style: normal;
    font-weight: 700;
    font-size: 16px;
    line-height: 20px;
    margin-bottom: 0px;
`;

const SearchResults = styled.div`
    width: 100%;
    height: 200px;
    background-color: white;
    box-shadow: rgba(0, 0, 0, 0.35) 0px 5px 15px;
    overflow: hidden;
    overflow-y: auto;
    &::-webkit-scrollbar {
        display: none;
      }
`;

const productItem = styled(Link)`
    width: 100%;
    height: 70px;
    display: flex;
    align-items: center;
    color: black;
    text-decoration: none;
    cursor: pointer;
    &:hover {
        background-color: lightgrey;
      }
    img {
        max-width:100%;
        max-height:100%;
    }
`;

/*const LeftIframe = styled(Iframe)`
    border: none;
    &::-webkit-scrollbar {
        display: none;
      }
`;*/

/*const SearchButton = styled.button`
    display: flex;
    flex-direction: row;
    justify-content: center;
    align-items: center;
    padding: 10px 10px;
    gap: 10px;
    width: auto;
    height: 40px;
    background: #307FE2;
    border-radius: 8px;
    border: none;
    font-family: 'Roboto';
    font-style: normal;
    font-weight: 400;
    font-size: 16px;
    line-height: 19px;
    color: rgba(255, 255, 255, 1);
    margin: 0 auto;
    cursor: pointer;
`; */



function Compare({open, onClose, handle}) {

    const [filteredproduct, setFilteredproduct] = useState([]);
    const [wordEntered, setWordEntered] = useState(""); 
    const [showInfo, setShowInfo] = useState(true);

    const handleFilter = (event) => {
        const searchWord = event.target.value;
        setWordEntered(searchWord);
        const newFilter = data.filter((value) => {
          return value.name.toLowerCase().includes(searchWord.toLowerCase());
        });
    
        if (searchWord === "") {
          setFilteredproduct([]);
        } else {
          setFilteredproduct(newFilter);
        }
      };

    const appear = () => {
        handle()
        
    }   

    const {lid} = useParams();
    let produc = [];
    produc = data[lid-1];


    if(!open) return null
    return (
        <>
            <CompareForm>
                <InputForm shouldCloseOnOverlayClick={false}>
                    <LabelLine>
                        <LabelSearch>Nhập tên sản phẩm</LabelSearch>
                        <img onClick={onClose} src={closeSearch} className="close-outline-btn" alt=""/>
                    </LabelLine>
                        <input 
                            type="text" 
                            value={wordEntered}
                            placeholder="Robot..."
                            onChange={handleFilter}
                        />
                        {filteredproduct.length !== 0 && (
                            <SearchResults>
                                {filteredproduct.slice(0, 15).map((value, key) => {                        
                                    return (
                                        <productItem to={`/detail/${value._id}`} onClick={appear} target="_blank">
                                            <img src={value.image} alt="" />
                                            <p>{value.name}</p>
                                        </productItem>
                                    )
                                })}
                            </SearchResults>
    
                            )}
                </InputForm>               
            </CompareForm>    
            {/*<div className="product3">
                <div className="center">
                <img src={product.image} alt="" />
                <img src={require("../detail_img/2.png")} style={{ width: "100%" }} alt="" />
                </div>
                <div className="under">
                <div className="price">
                    <ion-icon className="price-tag-icon" name="pricetags-outline" />
                    <div className="price-text">Giá bán</div>
                    <div className="price-self">{product.priceOn}</div>
                </div>
                <div className="more">
                    <ion-icon name="heart-circle-outline" className="heart-btn" />
                    <ion-icon name="share-social-outline" className="share-btn" />
                </div>
                </div>
                <form action="" className="style-form">
                Màu sắc
                <input type="radio" name="form-detail" defaultValue="den" />
                <label>Đen</label>
                <input type="radio" name="form-detail" defaultValue="xam" />
                <label>Xám</label>
                </form>
                <div className="name-product">{product.name}</div>
                <div className="btn-group">
                <button className="btn-shopping"><ion-icon name="cart-outline" class="shopping-cart"></ion-icon>Mua ngay</button>
                </div>
                <div className="product-describe">
                Thông tin mô tả
                <ul>
                    <p>{product.title}</p>
                    {
                        product.describe.map((line) =>
                        <li>{line}</li>
                        )
                    }
                </ul> 
                </div>
                <div className="more-detail">
                <div className="detail">Thông tin chi tiết</div>
                <div className="hide" onClick={() => setShowInfo(!showInfo)}>
                    {showInfo===true? "Ẩn bớt" : "Xem thêm"}
                    {showInfo===true? <ion-icon name="chevron-up-outline" /> : <ion-icon name="chevron-down-outline" />}
                </div>
                </div>
                {showInfo && <div className="detail-product">
                <div className="detail-product-item">
                    <div className="field">Hạn bảo hành:</div>
                    <div className="desc">12 tháng</div>
                </div>
                <div className="detail-product-item">
                    <div className="field">Kích thước:</div>
                    <div className="desc">340 x 85 x 340 mm</div>
                </div>
                <div className="detail-product-item">
                    <div className="field">Xuất xứ:</div>
                    <div className="desc">Trung Quốc</div>
                </div>
                <div className="detail-product-item">
                    <div className="field">Dung tích chứa bụi:</div>
                    <div className="desc">200 ml</div>
                </div>
                <div className="detail-product-item">
                    <div className="field">Khối lượng:</div>
                    <div className="desc">3 kg</div>
                </div>
                <div className="detail-product-item">
                    <div className="field">Dung lượng pin:</div>
                    <div className="desc">3400mAh</div>
                </div>
                <div className="detail-product-item">
                    <div className="field">Lực hút:</div>
                    <div className="desc">5W (~4200pa)</div>
                </div>
                <div className="detail-product-item">
                    <div className="field">Màu sắc:</div>
                    <div className="desc">Đen, Xám</div>
                </div>
                <div className="detail-product-item">
                    <div className="field">Tính năng:</div>
                    <div className="desc">
                    Điều khiển qua ứng dụng , Tự động quay về đế sạc , Tự động điều chỉnh
                    sức hút
                    </div>
                </div>
                <div className="detail-product-item">
                    <div className="field">Tiện ích:</div>
                    <div className="desc">
                    Hộp chứa bụi EZ - dễ dàng vệ sinh với nướcLên lịch làm việc và theo dõi
                    thông qua app kết nối với điện thoại thông minh
                    </div>
                </div>
                </div>}
            </div>*/}            
        </>
    )
}


export default Compare
